import React, { useEffect } from 'react';
import gsap from 'gsap'
// import { useGSAP } from '@gsap/react';
// gsap.registerPlugin(useGSAP);
import { ScrollTrigger } from 'gsap/ScrollTrigger';
gsap.registerPlugin(ScrollTrigger);

const Gsap = () => {
    const { contextSafe } = useGSAP();
    const spin = contextSafe(({ currentTarget }) => {
        gsap.to(currentTarget, { rotation: "+=90", repeat: 1, x: 100, duration: 1, ease: 'elastic.out' });
        gsap.to(currentTarget, { rotation: "+=360", x: 100, duration: 1 });
        gsap.to(currentTarget, { rotation: "+=360", x: 100, duration: 1 });
        gsap.from(currentTarget, { rotation: "-=360", });
    });
    // tl = gsap
    //     .timeline({
    //         scrollTrigger: {
    //             trigger: "#textSection",
    //             start: "top top",
    //             end: "+=150%",
    //             pin: true,
    //             scrub: 0.75,
    //             markers: true
    //         }
    //     })
    useEffect(() => {
        // GSAP ScrollTrigger animation for text color change
        gsap.to('.scroll-text', {
            scrollTrigger: {
                trigger: '.scroll-text',  // Element to trigger on scroll
                start: 'top 80%',          // Start when the top of the element reaches 80% of the viewport height
                end: 'bottom 20%',         // End when the bottom of the element reaches 20% of the viewport height
                scrub: true,               // Smooth animation as you scroll
                markers: false             // Optional: Set to true to show markers for debugging (can remove in production)
            },
            color: 'yellow',  // The color to change to when the scroll trigger is active
            duration: 1        // Duration of the color change (optional)
        });
    }, []);

    return (
        <div className='py-12 overflow-hidden'>
            <div className='container'>
                <h3 className='font-inter font-semibold text-4xl text-black text-center pb-9'>Gsap</h3>
                <div className="box bg-black max-w-[100px] h-20 flex justify-center items-center rounded mx-auto text-white" onClick={spin}>Click me
                </div>
                <div>
                    <p className="text-2xl font-medium max-w-[600px] mx-auto scroll-text">
                        Lorem ipsum dolor sit amet consectetur adipisicing elit. Ducimus deleniti praesentium veritatis. Perspiciatis sint molestias et corrupti, quasi provident earum!
                    </p> </div>
            </div>
        </div>
    )
}

export default Gsap
