import React, { useRef } from 'react';
import emailjs from '@emailjs/browser';

export const EmailjsPractice = () => {
    const form = useRef();

    const sendEmail = (e) => {
        e.preventDefault();

        emailjs
            .sendForm('service_ix25dr9', 'template_ohy6dpx', form.current, {
                publicKey: 'nzZC9f1fhink2qm3L',
            })
            .then(
                () => {
                    console.log('SUCCESS!');
                },
                (error) => {
                    console.log('FAILED...', error.text);
                },
            );
    };

    return (
        <div>
            <p className='font-semibold font-inter text-3xl text-center mx-auto pb-6'>EMAILJS</p>
            <form ref={form} onSubmit={sendEmail}>
                <label>Name</label>
                <input type="text" name="name" />
                <label>Email</label>
                <input type="email" name="user_email" />
                <label>Message</label>
                <textarea name="message" />
                <input type="submit" value="Send" />
            </form>
      </div>
    );
};
export default EmailjsPractice
