import React,{useEffect} from 'react'
import { useLocation } from 'react-router-dom'
import CommonHeading from '../common/CommonHeading'
import { useTypewriter } from 'react-simple-typewriter'
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
gsap.registerPlugin(ScrollTrigger);

const Hero = () => {
    const pathname = useLocation().pathname;
    const [ text ] = useTypewriter({
        words: ["World!","It's a typewriter effect."],
        loop: {},
        typeSpeed: 120,
        deleteSpeed: 40,
    });
    useEffect(() => {
        const heroElement = document.querySelector('.hero');
        if (heroElement) {
            gsap.fromTo(
                heroElement,
                {
                    scale: 1,
                    opacity: 1,
                    zIndex: 3,
                },
                {
                    scale: 1.1,
                    opacity: 1,
                    scrollTrigger: {
                        trigger: heroElement,
                        start: 'top bottom',
                        end: 'bottom top',
                        scrub: true,
                        onEnter: () => gsap.to(heroElement, { zIndex: 10 }),
                        onLeave: () => gsap.to(heroElement, { zIndex: 1 }),
                    },
                }
            );
        }
    }, []);
    
    return (
        <div className={`hero sticky top-0 overflow-hidden ${pathname === "/" ? "bg-orange rounded-b-xl" : pathname === "/about" ? "bg-dark-blue" : ""}`}>
                <div className='container'>
                    <div className='flex items-center justify-center w-full h-screen'>
                        <div>
                            <CommonHeading text="Lorem ipsum dolor sit amet consectetur." yellowText="Sceleris"></CommonHeading>
                            <p className='font-lato font-medium text-base text-white text-center mx-auto pt-5'>Lorem ipsum dolor sit amet consectetur. Id mattis at tristique</p>
                            <p className='text-white mx-auto text-2xl text-center pt-2'>Hello <span>{text}</span></p>
                        </div>
                    </div>
                </div>
            </div>
    )
}

export default Hero
