import React, { useState } from 'react'

const FormValidation = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: '',
    agree: false,
    selectColor: '',
    number: ''
  });
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Submitted:', formData);
  };
  return (
    <div className='py-12'>
      <div className='container'>
        <h3 className='font-inter font-semibold text-4xl text-black text-center pb-9'>Form Validation</h3>
        <form onSubmit={handleSubmit} action="" className='flex flex-col justify-center items-center gap-3'>
          {/* <label className="text-black">First Name</label> */}
          <input
            placeholder="Name"
            type="text"
            name="firstName"
            value={formData.firstName}
            onChange={handleChange}
            className="p-2 border border-black w-96 rounded outline-none"
          />
          {/* <input onChange={handleChange} value={formData.firstName} type="text" placeholder="Name" className='border border-black p-2 w-96 outline-none rounded' /> */}
          {/* {errors.name && <div className="text-red-500">{errors.name}</div>} */}
          {/* <input type="text" placeholder='Email' className='border border-black p-2 w-96 outline-none rounded' />
          {errors.email && <div className="text-red-500">{errors.email}</div>}
          <input type="text" placeholder='Password' className='border border-black p-2 w-96 outline-none rounded' />
          {errors.password && <div className="text-red-500">{errors.password}</div>}
          <input type="text" placeholder='Confirm Password' className='border border-black p-2 w-96 outline-none rounded' />
          {errors.confirmPassword && <div className="text-red-500">{errors.confirmPassword}</div>} */}
        </form>
        <div className='mx-auto text-center mt-5'>
          <button type='submit' className='bg-black py-2 px-5 rounded text-white font-medium '>Submit</button>
        </div>
      </div>
    </div>
  )
}

export default FormValidation
