import React, { useEffect } from 'react';
import gsap from 'gsap';
// import { ScrollTrigger } from 'gsap/ScrollTrigger';

// gsap.registerPlugin(ScrollTrigger);

const TextAnimation = () => {
    useEffect(() => {
        // Split the text into individual letters and wrap each one in a span
        const text = document.querySelector('.animated-text');
        const letters = text.textContent.split('');
        text.innerHTML = letters.map(letter => `<span>${letter}</span>`).join('');

        // Animate the color change of each letter
        gsap.fromTo(
            '.animated-text span',
            { color: 'black' },   // Starting color (black)
            {
                color: 'yellow',    // Final color (yellow)
                duration: 0.5,      // Duration for the color change
                stagger: 0.1,       // Stagger the animation for each letter by 0.1 seconds
            }
        );
    }, []);
  return (
      <div className='py-12'><p className='text-5xl text-black font-medium text-center'>TextAnimation</p>
          <div>
              <p className='font-5xl text-black max-w-[550px] mx-auto pt-5 animated-text'>Lorem ipsum dolor sit amet consectetur adipisicing elit. Maxime ad possimus sunt impedit temporibus. Velit quas quaerat repudiandae atque illo.
              </p></div>
      </div>
  )
}

export default TextAnimation
