import React, { useEffect, useState } from 'react'

const BackToTop = () => {
    const [scrollTop, setScrollTop] = useState(false)
    const scrollHandler = () => {
        window.scroll({ top: 0, behavior: "smooth" })
    }
    const scrollTopHandler = () => {
        setScrollTop(window.scrollY >= 400)
    }
    useEffect(() => {
        // Add the scroll event listener
        window.addEventListener("scroll", scrollTopHandler);

        // Cleanup the event listener on component unmount
        return () => {
            window.removeEventListener("scroll", scrollTopHandler);
        };
    }, []);
    return (
        <div>
            {scrollTopHandler && (
                <p onClick={scrollHandler} className='font-medium text-red-400 fixed right-3 bottom-3'>Top</p>
            )}
        </div>
    )
}

export default BackToTop
