import React from 'react'

const TextAnimation = () => {
    useEffect(() => {
        // GSAP ScrollTrigger animation for text color change
        gsap.to('.scroll-text', {
            scrollTrigger: {
                trigger: '.scroll-text',  // Element to trigger on scroll
                start: 'top 80%',          // Start when the top of the element reaches 80% of the viewport height
                end: 'bottom 20%',         // End when the bottom of the element reaches 20% of the viewport height
                scrub: true,               // Smooth animation as you scroll
                markers: false             // Optional: Set to true to show markers for debugging (can remove in production)
            },
            color: 'yellow',  // The color to change to when the scroll trigger is active
            duration: 1        // Duration of the color change (optional)
        });
    }, []);
  return (
      <div className='py-12'><p className='text-5xl text-black font-medium text-center'>TextAnimation</p>
          <div>
              <p className='font-2xl text-black max-w-[550px] mx-auto pt-5 scroll-text'>Lorem ipsum dolor sit amet consectetur adipisicing elit. Maxime ad possimus sunt impedit temporibus. Velit quas quaerat repudiandae atque illo.
              </p></div>
      </div>
  )
}

export default TextAnimation
