  return (
    <div className='py-12'>
      <div className='container'>
        <h3 className='font-inter font-semibold text-4xl text-black text-center pb-9'>Counter Practice </h3>
        {/* use state      */}
        <div className='text-center '>
          <p className='text-lg text-black opacity-60'>{name}</p>
          <button onClick={() => setName(name => (name === 'rajni' ? 'dikhsa' : 'rajni'))}
            className='font-medium text-lg bg-blue p-2 rounded mt-3'>Toggle Button</button>
        </div>

        {/* use effect 
        <div className='text-center '>
          <p className='text-lg text-black opacity-60'>{number}</p>
          <button onClick={() => setNumber(number => (number==='1'? '10':'12'))}
            className='font-medium text-lg bg-blue p-2 rounded mt-3'>Button</button>
        </div>  */}

        {COUNTER.map((number, text) => (
          <div key={text} className='lg:w-[33.33%] sm:w-[50%] w-full px-3 flex justify-center lg:pb-6 pb-5'>
