import React, { useEffect } from 'react';
import gsap from 'gsap';
// import { ScrollTrigger } from 'gsap/ScrollTrigger';

// gsap.registerPlugin(ScrollTrigger);

const TextAnimation = () => {
    useEffect(() => {
        const text = document.querySelector('.animated-text');
        const letters = text.textContent.split('');
        text.innerHTML = letters.map(letter => `<span>${letter}</span>`).join('');

        gsap.fromTo(
            '.animated-text span',
            { color: 'black' }, 
            {
                color: 'blue',   
                duration: 0.5,    
                stagger: 0.1,     
                repeat:-1,
            },
        );
    }, []);
    useEffect(() => {
        const text = document.querySelector('.animated-text1');
        const letters = text.textContent.split('');
        text.innerHTML = letters.map(letter => `<span>${letter}</span>`).join('');
        gsap.fromTo(
            '.animated-text1',
            { color: 'black' },  
            {
                color: 'red',  
                duration: 6,     
                stagger: 0.1,      
                repeat: -1,
            },
        );
    }, []);
    useEffect(() => {
        // GSAP animation to move the text from bottom to top
        gsap.from('.animated-text', {
            y: 100,          // Start position (move 100px from the bottom)
            opacity: 0,      // Start with opacity 0 (invisible)
            duration: 1,     // Animation duration (1 second)
            ease: 'power3.out' // Ease for smooth animation
        });
    }, []);
    
  return (
      <div className='py-12'><p className='text-5xl text-black font-medium text-center animated-text1'>TextAnimation</p>
          <div>
              <p className='text-4xl text-black max-w-[650px] text-center mx-auto pt-5 animated-text'>Lorem ipsum dolor sit amet consectetur adipisicing elit.
              </p></div>
      </div>
  )
}

export default TextAnimation
