import React from 'react'
import { useState } from 'react';
import { RED_CARDS } from './Helper';

const Cards = () => {
    const [view, setView] = useState(false);
    const toggleView = () => {
        setView(!view)
    }
    const cardsToShow = RED_CARDS.slice(0, view ? 6 : 3);
  return (
      <div className='py-12'>
          <div className='container'>
              <h3 className='font-inter font-semibold text-4xl text-black text-center pb-10'>Cards </h3>
              <div className="flex flex-row flex-wrap -mx-3 gap-2 justify-center">
                  {cardsToShow.map.map((card, index) => (
                      <div key={index} className="md:w-[33.33%] sm:w-[50%] w-full px-3 bg-red-500 max-w-[300px] h-[200px]">
                          <P>{number}</P>
                      </div>
                  ))}
              </div>
              <div className='text-center mt-5'>
                  <button className='p-3 font-bold text-lg bg-blue rounded' onClick={toggleView}>View Button</button>
              </div>
        </div>
    </div>
  )
}

export default Cards
