import React, { useEffect, useState } from 'react';

const BackToTop = () => {
    const [scrollTopVisible, setScrollTopVisible] = useState(false);

    const scrollHandler = () => {
        window.scrollTo({ top: 0, behavior: "smooth" });
    };

    const scrollTopHandler = () => {
        setScrollTopVisible(window.scrollY >= 100);
    };

    useEffect(() => {
        window.addEventListener("scroll", scrollTopHandler);

        return () => {
            // window.removeEventListener("scroll", scrollTopHandler);
        };
    }, []); 

    return (
        <div>
            {scrollTopVisible && (
                <p
                    onClick={scrollHandler}
                    className='font-medium text-red-400 fixed right-3 bottom-3 cursor-pointer'>
                    Top
                </p>
            )}
        </div>
    );
};

export default BackToTop;
