import React from 'react'
import gsap from 'gsap'
import { useGSAP } from '@gsap/react';
gsap.registerPlugin(useGSAP);

const Gsap = () => {
    const { contextSafe } = useGSAP();
    const spin = contextSafe(({ currentTarget }) => {
        gsap.to(currentTarget, { rotation: "+=90", repeat: 1, x: 100, duration: 1, ease: 'elastic.out' });
        gsap.to(currentTarget, { rotation: "+=360", x: 100, duration: 1 });
        gsap.to(currentTarget, { rotation: "+=360", x: 100, duration: 1 });
        // gsap.from(currentTarget, { rotation: "-=360", });
    });
    var tl = gsap.timeline({ delay: 0.5 });
    //sequenced one-after-the-other
    tl.to(".box1", { duration: 5, repeat: 9, x: 100, rotation: 360, ease: 'elastic.out' })
        .to(".box2", { duration: 1, x: -100, ease: 'elastic.out' })
        .to(".box3", { duration: 2, rotation: 360, x: 100, ease: 'expo.out' });
  return (
    <div className='py-12 overflow-hidden'>
          <div className='container'>
              <h3 className='font-inter font-semibold text-4xl text-black text-center pb-9'>Gsap</h3>
                  <div className="box bg-black max-w-[100px] h-20 flex justify-center items-center rounded mx-auto" onClick={spin}>
              </div>
              <div className="box1 bg-pink-600 mt-5 max-w-[100px] h-20 flex justify-center items-center rounded mx-auto">
              </div>
        </div>
    </div>
  )
}

export default Gsap
