import React, { useState } from 'react'
import CountUp from 'react-countup'
import ReactVisibilitySensor from 'react-visibility-sensor'

const CounterPractice = () => {
    const [hasVisible, setHasVisible] = useState(false);
    const [ value,setView] = useState(0)
    function onVisible(isVisible) {
        if (isVisible) {
            setHasVisible(true); // Set to true when the section is visible
        }
    }
    return (
        <div>
            <div className='container'>
                <p className='font-medium text-xl text-center'>Counter</p>
                <div className='text-center pt-5'>
                    <ReactVisibilitySensor onChange={onVisible}>
                        <div>
                            {hasVisible && (
                                <CountUp start={0} end={50} delay={1} duration={2} />
                            )}
                        </div>
                  </ReactVisibilitySensor>
               </div>
            </div>
        </div>
    )
}

export default CounterPractice
