import React, { useEffect, useRef } from "react";
import gsap from "gsap";
import { SplitText } from "../../node_modules";
gsap.registerPlugin(gsap.SplitText);

const TextAnimation = () => {
    const quoteRef = useRef(null);
    const animateButtonRef = useRef(null);
    useEffect(() => {
        // Register SplitText plugin
        gsap.registerPlugin(SplitText);

        // Split text into words and characters
        const mySplitText = new SplitText(quoteRef.current, { type: "words,chars" });
        const chars = mySplitText.chars; // This is an array of divs that wrap each character

        // Set the perspective for the #quote element
        gsap.set(quoteRef.current, { perspective: 400 });

        // Create the animation timeline
        const tl = gsap.timeline();
        tl.from(chars, {
            duration: 0.8,
            opacity: 0,
            scale: 0,
            y: 80,
            rotationX: 180,
            transformOrigin: "0% 50% -50",
            ease: "back",
            stagger: 0.01
        });
        // Button click to restart the animation
        const animateButton = animateButtonRef.current;
        animateButton.onclick = () => {
            tl.restart();
        };
        return () => {
            // Clean up when component unmounts
            mySplitText.revert(); // Revert SplitText when the component is removed
        };
    }, []);
  return (
      <div>
          <div id="quote" ref={quoteRef}>
              SplitText makes it easy to break apart the text in an HTML element so that each character, word, and/or line is wrapped in its own div tag.
          </div>
          <button id="animate" ref={animateButtonRef}>animate</button>
      </div>
  )
}

export default TextAnimation
