import React, { useEffect } from 'react'
import { MAP_CARDS } from '../../common/Helper'
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
gsap.registerPlugin(ScrollTrigger);

const MapCards = () => {
  useEffect(() => {
    const heroElement = document.querySelector('.hero');
    if (heroElement) {
      gsap.fromTo(
        heroElement,
        {
          scale: 1,
          opacity: 1,
          zIndex: 3,
        },
        {
          scale: 1.1,
          opacity: 1,
          scrollTrigger: {
            trigger: heroElement,
            start: 'top bottom',
            end: 'bottom top',
            scrub: true,
            onEnter: () => gsap.to(heroElement, { zIndex: 10 }),
            onLeave: () => gsap.to(heroElement, { zIndex: 1 }),
          },
        }
      );
    }
  }, []);
  return (
    div
    
    
  )
}

export default MapCards
