import React, { useState, useEffect } from 'react'
import preLoaderImg from '../assets/images/png/preloader-image.png'

const PreLoader = () => {
const [pre, setPre]
    = useState(true)
  useEffect(() => {
    const timer = setTimeout(() => {
      setPre(false)
    },1000)
  },[])

  return (
    <div>
      {pre ? (
        <div className='h-screen w-full bg-blue fixed bottom-0 top-0 right-0 z-40 flex justify-center items-center overflow-hidden'>
          <div className='flex justify-between items-center text-xl font-bold overflow-hidden text-black'>
            <img src={preLoaderImg} alt="preloader" />
          </div>
        </div>
      ):null}
  </div>
  )
}

export default PreLoader
