    return (
        <div className='bg-our-partners bg-cover bg-no-repeat bg-center'>
            <div className='flex flex-col justify-center items-center max-w-[1200px] w-full mx-auto'>
                <p className='text-[80px] leading-[80px]'>our partners</p>
                <img src={weWorlImg} alt="work" className='w-[444px]' />
                <div className='flex flex-wrap flex-row -mx-3 justify-center'>
                    {OUR_PARTNERS.map((obj, index) => (
                        <div key={index} className='lg:w-[33.33%] sm:w-[50%] w-full px-3 flex justify-center lg:pb-6 pb-5'>
                            <div className='max-w-[360px] w-full bg-white'>
                                <img src={obj.image} alt="svg" />
                            </div>
                        </div>
                    ))}
                </div>
               
            </div>
        </div>
