import React, { useEffect } from 'react';
import gsap from 'gsap';
// import { ScrollTrigger } from 'gsap/ScrollTrigger';

gsap.registerPlugin(ScrollTrigger);

const TextAnimation = () => {
    useEffect(() => {
        const text = document.querySelector('.animated-text');
        const letters = text.textContent.split('');
        text.innerHTML = letters.map(letter => `<span>${letter}</span>`).join('');

        gsap.fromTo(
            '.animated-text span',
            { color: 'black' }, 
            {
                color: 'blue',   
                duration: 0.5,    
                stagger: 0.1,     
                repeat:-1,
            },
        );
    }, []);
    useEffect(() => {
        const text = document.querySelector('.animated-text1');
        const letters = text.textContent.split('');
        text.innerHTML = letters.map(letter => `<span>${letter}</span>`).join('');
        gsap.fromTo(
            '.animated-text1',
            { color: 'black' },  
            {
                color: 'red',  
                duration: 6,     
                stagger: 0.1,      
                repeat: -1,
            },
        );
        ScrollTrigger.create({
            trigger: '.animated-text1',  // The section to pin
            start: 'top top',             // When the top of the element reaches the top of the viewport
            end: '+=600',                 // Pin for 600px of scroll (you can adjust this value)
            pin: true,                    // Pin the section during the animation
            pinSpacing: false,            // Disable spacing after pin (keeps the layout consistent)
        });
    }, []);
    useEffect(() => {
        // GSAP animation to move the text from right to center
        gsap.fromTo(
            '.animated-text',
            {
                x: '100%',          // Start position (off-screen to the right)
                opacity: 0,         // Start with opacity 0 (invisible)
            },
            {
                x: 0,               // Final position (center)
                opacity: 1,         // Final opacity (fully visible)
                duration: 6,        // Duration of the animation (1 second)
                ease: 'power3.out', // Ease function for smooth animation
                repeat: -1,          // Only animate once
            }
        );
    }, []);
    
  return (
      <div className='py-12'><p className='text-5xl text-black font-medium text-center animated-text1'>TextAnimation</p>
              <p className='text-4xl text-black max-w-[650px] text-center mx-auto pt-5 animated-text'>Lorem ipsum dolor sit amet consectetur adipisicing elit.
              </p>
      </div>
  )
}

export default TextAnimation
