import React, { useEffect, useState } from 'react';

const BackToTop = () => {
    const [scrollTopVisible, setScrollTopVisible] = useState(false);

    // Scroll handler to scroll back to the top smoothly
    const scrollHandler = () => {
        window.scrollTo({ top: 0, behavior: "smooth" });
    };

    // Scroll event handler to show/hide the "Back to Top" button
    const scrollTopHandler = () => {
        setScrollTopVisible(window.scrollY >= 100); // Show button if scrolled 400px or more
    };

    useEffect(() => {
        // Add the scroll event listener
        window.addEventListener("scroll", scrollTopHandler);

        // Cleanup the event listener on component unmount
        return () => {
            window.removeEventListener("scroll", scrollTopHandler);
        };
    }, []); // Empty dependency array means this effect runs once on mount

    return (
        <div>
            {scrollTopVisible && (
                <p
                    onClick={scrollHandler}
                    className='font-medium text-red-400 fixed right-3 bottom-3 cursor-pointer'>
                    Top
                </p>
            )}
        </div>
    );
};

export default BackToTop;
