    return (
        <div className='bg-our-partners bg-cover bg-no-repeat bg-center lg:pt-[113px] pt-16 pb-[66px] lg:pb-[311px]'>
            <div className='container'>
                <div className='flex flex-col justify-center items-center max-w-[1200px] w-full mx-auto'>
                    <p className='text-[80px] leading-[80px]'>our partners</p>
                    <img src={weWorlImg} alt="work" className='w-[444px] lg:pb-[100px] ' />
                    <div className='flex flex-wrap flex-row -mx-3 justify-center w-full'>
                        {OUR_PARTNERS.map((obj, index) => (
                            <div key={index} className='lg:w-[33.33%] sm:w-[50%] w-full px-3 flex justify-center items-center lg:pb-6 pb-5'>
                                <div className='max-w-[360px] w-full h-[140px] flex items-center justify-center bg-white shadow shadow-black bg-strake bg-cover bg-center bg-no-repeat'>
                                    <img src={obj.image} alt="svg" className='w-[131px] ' />
