import React, { useState } from 'react'
import swal from 'sweetalert';

const FormValidation = () => {
  const [value, setValue] = useState(formData)
  const formData = {
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
  }
  // const [formData, setFormData] = useState({
  //   name: "",
