import React, { useState } from 'react'

const CounterPractice = () => {
    // const [value,setValue]=useState(0)
    const [name, setName] = useState('Sima');

    const toggleName = () => {
        setName(prevName => (prevName === 'Sima' ? 'Ritu' : 'Sima'));
    };
  return (
    <div className='py-12'>
          {/* <div className='container'>
              <h3 className='font-inter font-semibold text-4xl text-black text-center pb-9'>Counter Practice </h3>
              <div className='text-center '>
                  <p className='text-lg text-black opacity-60'>{value}</p>
                  <button onClick={() => setValue(value + 1)} className='font-medium text-lg bg-blue p-2 rounded mt-3'>Toggle Button</button>
             </div>
          </div> */}
          <div>
              <h1>{name}</h1>
              <button onClick={toggleName}>Toggle Name</button>
          </div>
    </div>
  )
}

export default CounterPractice
