import React from 'react'
import gsap from 'gsap'
import { useGSAP } from '@gsap/react';
gsap.registerPlugin(useGSAP);
// import { ScrollTrigger } from 'gsap/ScrollTrigger';
// import { SplitText } from 'gsap/SplitText';
// import{createSplit} from 'gsap/'
// let split, tl;
const { useEffect, useRef } = React;


const Gsap = () => {
    const { contextSafe } = useGSAP();
    const spin = contextSafe(({ currentTarget }) => {
        gsap.to(currentTarget, { rotation: "+=90", repeat: 1, x: 100, duration: 1, ease: 'elastic.out' });
        gsap.to(currentTarget, { rotation: "+=360", x: 100, duration: 1 });
        gsap.to(currentTarget, { rotation: "+=360", x: 100, duration: 1 });
        // gsap.from(currentTarget, { rotation: "-=360", });
    });
    useEffect(() => {
        // SplitText
        const split = new SplitText(document.querySelector('.title'), { type: 'lines words', linesClass: 'line line--++', wordsClass: 'word word--++' });

        // set the words to initialize before animating 
        gsap.set(document.querySelectorAll('.title .word'), { opacity: 0.0001, y: '200%', rotation: '3deg' });
        gsap.set(document.querySelectorAll('.title'), { opacity: 1 });

        // animate In the words
        gsap.to(document.querySelectorAll('.title .word'), { opacity: 1, y: '0%', rotation: '0deg', ease: 'power4.inOut', duration: 1.2, stagger: 0.01 });

        // animate Out the words
        gsap.to(document.querySelectorAll('.title .word'), { opacity: 0.0001, y: '-200%', rotation: '0deg', ease: 'power4.inOut', duration: 1.2, stagger: 0.01, delay: 5 });

    });
