import React, { useState } from 'react'

const CounterPractice = () => {
  const [name, setName] = useState('rajni');
  const toggleName = () => {
    setName(name => (name === 'rajni' ? 'dikhsa' : 'rajni'));
  };
  // const [number, setNumber] = useState(false);
  useEffect(() => {
    // Start an interval to update the seconds state every 1000ms (1 second)
    const interval = setInterval(() => {
      setSeconds((prevSeconds) => prevSeconds + 1);
    }, 1000);

    // Cleanup function to clear the interval when the component unmounts
    return () => {
      clearInterval(interval);
    };
  }, []);
  return (
    <div className='py-12'>
      <div className='container'>
        <h3 className='font-inter font-semibold text-4xl text-black text-center pb-9'>Counter Practice </h3>
        {/* use state      */}
        <div className='text-center '>
          <p className='text-lg text-black opacity-60'>{name}</p>
          <button onClick={toggleName} className='font-medium text-lg bg-blue p-2 rounded mt-3'>Toggle Button</button>
        </div>

        {/* use effect  */}
        <div className='text-center '>
          <p className='text-lg text-black opacity-60'>{number}</p>
          <button onClick={() => {
            setTimeout(() => {
              setNumber(number => (number==='1'? '10':''));
            },1000);
          }} className='font-medium text-lg bg-blue p-2 rounded mt-3'>Button</button>
        </div>
      </div>
    </div>
  )
}

export default CounterPractice
