import React, { useState, useEffect } from 'react'
import { BLUE_CARD } from '../common/Helper'

const CounterPractice = () => {
  const [name, setName] = useState('rajni');
  // const [count, setCount] = useEffect(0);
  // useEffect(() => {
  //   setCount(count => (count + 1));
  // }, 1000)

  const [view, setView] = useState('');
  return (
    <div className='py-12'>
      <div className='container'>
        <h3 className='font-inter font-semibold text-4xl text-black text-center pb-9'>Counter Practice </h3>
        {/* use state      */}
        <div className='text-center '>
          <p className='text-lg text-black opacity-60'>{name}</p>
          <button onClick={() => setName(name => (name === 'rajni' ? 'dikhsa' : 'rajni'))}
            className='font-medium text-lg bg-blue p-2 rounded mt-3'>Toggle Button</button>
        </div>

        {/* use effect  */}
        {/* <div className='text-center '>
          <p className='text-lg text-black opacity-60'>{count}</p>
          <button
            className='font-medium text-lg bg-blue p-2 rounded mt-3'>Button</button>
        </div> */}

        <div className="flex w-full gap-2 justify-between">
          {[...Array(view ? 3 : 0)].map((card, index) => (
          <div key={index} className="bg-red-500 w-full max-w-[300px] h-[200px]">
            <div className="flex justify-center items-center h-full">
              {card}
            </div>
          </div>
          ))}
        </div>
        <button className='' onClick={setView}>View Button</button>

      </div>
    </div>
  )
}

export default CounterPractice
