import React from 'react'
import gsap from 'gsap'
import { useGSAP } from '@gsap/react';
gsap.registerPlugin(useGSAP);

const Gsap = () => {
    const { contextSafe } = useGSAP();
    const spin = contextSafe(({ currentTarget }) => {
        gsap.to(currentTarget, { rotation: "+=90", repeat: 1, x: 100, duration: 1, ease: 'elastic.out' });
        gsap.to(currentTarget, { rotation: "+=360", x: 100, duration: 1 });
        gsap.to(currentTarget, { rotation: "+=360", x: 100, duration: 1 });
        // gsap.from(currentTarget, { rotation: "-=360", });
    });
    // var tl = gsap.timeline({ delay: 1,repeat:-1 });
    // tl.to(".box1", { duration: 5, repeat: -3, x: 100, rotation: 360, ease: 'elastic.out' })
    //     .to(".box2", { duration: 1, x: -100, repeat: -3, ease: 'elastic.out' })
    //     .to(".box3", { duration: 2, rotation: 360, x: 100, ease: 'expo.out' });
    let tl = gsap.timeline({ defaults: { ease: "SlowMo.easeOut" } });
    tl.to("#create", { y: '0%', duration: 0.7, stagger: 0.2, });
    tl.to(".main", { y: '100%', duration: 1.5, delay: 0.2, });
    tl.fromTo("h1", { opacity: 0 }, { opacity: 1, delay: 0.5 }); 
    return (
        <div className='py-12 overflow-hidden'>
            <div className='container'>
                <h3 className='font-inter font-semibold text-4xl text-black text-center pb-9'>Gsap</h3>
                <div className="box bg-black max-w-[100px] h-20 flex justify-center items-center rounded mx-auto text-white" onClick={spin}>Click me
                </div>
                {/* <div className="box1 bg-pink-600 mt-5 max-w-[100px] h-20 flex justify-center items-center rounded mx-auto"></div>
                <div className="box2 bg-green-600 mt-5 max-w-[100px] h-20 flex justify-center items-center rounded mx-auto"></div>
                <div className="box3 bg-yellow-600 mt-5 max-w-[100px] h-20 flex justify-center items-center rounded mx-auto"></div> */}
            </div>
            <div class="main bg-green-300 fixed top-0 left-0 w-full h-full flex justify-center items-center">
                <div class="heading text-black text-3xl">
                    <h2 class="overflow-hidden font-bold">
                        <span id="create" class="inline-block translate-y-[100%]">1</span>
                    </h2>
                    <h2 class="overflow-hidden font-bold">
                        <span id="create" class="inline-block translate-y-[100%]">2</span>
                    </h2>
                    <h2 class="overflow-hidden font-bold">
                        <span id="create" class="inline-block translate-y-[100%]">3</span>
                    </h2>
                </div>
            </div>
        </div>
    )
}

export default Gsap
