import React from 'react'
import gsap from 'gsap'
import { useGSAP } from '@gsap/react';
gsap.registerPlugin(useGSAP);

const Gsap = () => {
    const { contextSafe } = useGSAP();
    const onEnter = contextSafe(({ currentTarget }) => {
        gsap.to(currentTarget, { rotation: "+=360",});
        // gsap.from(currentTarget, { rotation: "-=360", });
    });
  return (
    <div className='py-12 overflow-hidden'>
          <div className='container'>
              <h3 className='font-inter font-semibold text-4xl text-black text-center pb-9'>Gsap</h3>
                  <div className="box bg-black max-w-[100px] h-20 flex justify-center items-center rounded mx-auto" onClick={onEnter}>
                  </div>
        </div>
    </div>
  )
}

export default Gsap
