import React, { useState } from 'react'

const FormValidation = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: '',
    // agree: false,
    // selectColor: '',
    // number: ''
  });
  const [errors, setErrors] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: '',
    // agree: '',
    // selectColor: '',
    // number: ''
  });
  const validateForm = () => {
    const newErrors = {};

    if (!formData.firstName) {
      newErrors.firstName = 'First Name is required';
    }

    if (!formData.lastName) {
      newErrors.lastName = 'Last Name is required';
    }

    if (!formData.email) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.password) {
      newErrors.password = 'Password is required';
    }

    if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match';
    }

    if (!formData.agree) {
      newErrors.agree = 'You must agree to the terms and conditions';
    }

    if (!formData.selectColor) {
      newErrors.selectColor = 'Please select a color';
    }

    if (!formData.number) {
      newErrors.number = 'Number is required';
    }

    return newErrors;
  };
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Submitted:', formData);
  };
  return (
    <div className='py-12'>
      <div className='container'>
        <h3 className='font-inter font-semibold text-4xl text-black text-center pb-9'>Form Validation</h3>
        <form onSubmit={handleSubmit} action="" className='flex flex-col justify-center items-center gap-3'>
          <div className='flex flex-col'>
            <label className="text-black">First Name</label>
            <input
              placeholder="First Name"
              type="text"
              name="firstName"
              value={formData.firstName}
              onChange={handleChange} required
              className="p-2 border border-black w-96 rounded outline-none"
            />
            {errors.firstName && <p className="text-red-500">{errors.firstName}</p>}
</div>
          <input type="text"
            placeholder="Last Name"
              name="lastName"
              value={formData.lastName}
              onChange={handleChange}
            className="p-2 border border-black w-96 rounded outline-none" />
          <input
            type="email"
            placeholder="Email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className="p-2 border border-black w-96 rounded outline-none" />
          <input
            type="password" placeholder="Password" name="password" value={formData.password} onChange={handleChange}
            className="p-2 border border-black w-96 rounded outline-none" />
          <input
            type="password" placeholder="Confirm Password"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleChange}
            className="p-2 border border-black w-96 rounded outline-none" />
          {/* <div className="p-2 w-96">
            <input
              type="checkbox"
              name="agree"
              checked={formData.agree}
              onChange={handleChange}
              className="p-2 border border-black rounded outline-none"/>
            <label className="text-black ps-2">I agree to the terms and conditions</label>
          </div>
          <div className='w-96'>
          <label className="text-black">Select a color</label>
            <select
              name="selectOption"
              value={formData.selectOption}
              onChange={handleChange}
              className="p-2 border border-black w-96 rounded outline-none">
              <option value="Red">Black</option>
              <option value="BLue">Blue</option>
            </select>
          </div>
          <input
            type="number" placeholder="Number"
            name="number"
            value={formData.number}
            onChange={handleChange}
            className="p-2 border border-black w-96 rounded outline-none" /> */}
        </form>
        <div className='mx-auto text-center mt-5'>
          <button type='submit' className='bg-black py-2 px-5 rounded text-white font-medium hover:scale-95 duration-700'>Submit</button>
        </div>
      </div>
    </div>
  )
}

export default FormValidation
