import React, { useState } from 'react'

const FormValidation = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: '',
    agree: false,
    selectColor: '',
    number: ''
  });
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Submitted:', formData);
  };
  return (
    <div className='py-12'>
      <div className='container'>
        <h3 className='font-inter font-semibold text-4xl text-black text-center pb-9'>Form Validation</h3>
        <form onSubmit={handleSubmit} action="" className='flex flex-col justify-center items-center gap-3'>
          {/* <label className="text-black">First Name</label> */}
          <input
            placeholder="First Name"
            type="text"
            name="firstName"
            value={formData.firstName}
            onChange={handleChange}
            className="p-2 border border-black w-96 rounded outline-none"
          />
          <input type="text"
            placeholder="Last Name"
              name="lastName"
              value={formData.lastName}
              onChange={handleChange}
            className="p-2 border border-black w-96 rounded outline-none" />
          <input
            type="email"
            placeholder="Email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className="p-2 border border-black w-96 rounded outline-none" />
          <input
            type="password" placeholder="Password" name="password" value={formData.password} onChange={handleChange}
            className="p-2 border border-black w-96 rounded outline-none" />
          <input
            type="password" placeholder="Confirm Password"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleChange}
            className="p-2 border border-black w-96 rounded outline-none" />
          <div className="p-2 w-96">
            <input
              type="checkbox"
              name="agree"
              checked={formData.agree}
              onChange={handleChange}
              className="p-2 border border-black rounded outline-none"/>
            <label className="text-black ps-2">I agree to the terms and conditions</label>
          </div>
          <label className="text-black">Select a color</label>
          <select
            name="selectOption"
            value={formData.selectOption}
            onChange={handleChange}
            className="p-2 border border-black w-96 rounded outline-none">
            <option value="Red" className='w-96'>Red</option>
            <option value="BLue">Blue</option>
          </select>
        </form>
        <div className='mx-auto text-center mt-5'>
          <button type='submit' className='bg-black py-2 px-5 rounded text-white font-medium hover:scale-95 duration-700'>Submit</button>
        </div>
      </div>
    </div>
  )
}

export default FormValidation
