import React, { useState } from 'react'

const FormValidation = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
  });
  const [error, setError] = useState({
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
  });
  const [passwordShow, showSetPassword] = useState(false);
  const [confirmPasswordShow, confirmPasswordSet] = useState();
  const validateEmail = (email) => {
    const emailRegex = /^[a-zA-Z0-9_.+-]+@[a-zA-Z0-9-]+\.[a-zA-Z]{2,}/;
    return emailRegex.test(email);
  };
  const handelSubmit = (e) => {
    e.preventDefault();

    const errors = {
      name: formData.name ? "" : "name is required",
      email: formData.email ? (validateEmail(formData.email) ? "" : "invalid email") : "email is required",
      password: formData.Password ? "" : "Password is required",
      confirmPassword: formData.ConfirmPassword ?
        (formData.Password === formData.confirmPassword ? "" : "Password not match") : "Confirm password is required",
    };
    setError(errors);
    if (!Object.values(errors).some(error => error)) {
      console.log("Submitted values:");
      setFormData({
        name: "",
        email: "",
        password: "",
        confirmPassword: "",
      });
    }
  };
  // const [formData, setFormData] = useState({
  //   firstName: '',
  //   lastName: '',
  //   email: '',
  //   password: '',
  //   confirmPassword: '',
  //   // agree: false,
  //   // selectColor: '',
  //   // number: ''
  // });

  // const validateForm = () => {
  //   const newErrors = {};

  //   if (!formData.firstName) {
  //     newErrors.firstName = 'First Name is required';
  //   }

  //   if (!formData.lastName) {
  //     newErrors.lastName = 'Last Name is required';
  //   }

  //   if (!formData.email) {
  //     newErrors.email = 'Email is required';
  //   } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
  //     newErrors.email = 'Email is invalid';
  //   }

  //   if (!formData.password) {
  //     newErrors.password = 'Password is required';
  //   }

  //   if (formData.password !== formData.confirmPassword) {
  //     newErrors.confirmPassword = 'Passwords do not match';
  //   }

  //   if (!formData.agree) {
  //     newErrors.agree = 'You must agree to the terms and conditions';
  //   }

  //   if (!formData.selectColor) {
  //     newErrors.selectColor = 'Please select a color';
  //   }

  //   if (!formData.number) {
  //     newErrors.number = 'Number is required';
  //   }

  //   return newErrors;
  // };
  // const handleChange = (e) => {
  //   const { name, value, type, checked } = e.target;

  //   setFormData((prevData) => ({
  //     ...prevData,
  //     [name]: type === 'checkbox' ? checked : value
  //   }));
  // };

  // const handleSubmit = (e) => {
  //   e.preventDefault();
  //   console.log('Form Submitted:', formData);
  // };
  return (
    <div className='py-12'>
      <div className='container'>
        <h3 className='font-inter font-semibold text-4xl text-black text-center pb-9'>Form Validation</h3>
        <form onSubmit={handelSubmit} className='d-flex flex-column justify-content-center align-items-center'>
          <div className='flex flex-col gap-3 pt-2 justify-center items-center'>
            <div>
              <input
                type="text"
                placeholder='Name'
                className="p-2 border border-black w-96 rounded outline-none"
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                value={formData.name} />
              {error.name && <p className="mt-1 text-red-500">{error.name}</p>}
          </div>
            <div>
              <input
                type="email"
                placeholder='Email'
                className="p-2 border border-black w-96 rounded outline-none"
                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                value={formData.email} />
              {error.email && <p className="mt-1 text-red-500">{error.email}</p>}
       </div>
            <div>
              <input
                type="password"
                placeholder='Password'
                className="p-2 border border-black w-96 rounded outline-none"
                onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                value={formData.password} />
              {error.password && <p className="mt-1 text-red-500">{error.password}</p>}
           </div>
            <div>
              <input
                type="password"
                placeholder='Confirm Password'
                className="p-2 border border-black w-96 rounded outline-none"
                onChange={(e) => setFormData({ ...formData, confirmPassword: e.target.value })}
                value={formData.confirmPassword} />
              {error.confirmPassword && <p className="mt-1 text-red-500">{error.confirmPassword}</p>}
         </div>
          </div>
          <div className='mx-auto text-center mt-5'>
            <button type='submit' className='bg-black py-2 px-5 rounded text-white font-medium hover:scale-95 duration-700'>Submit</button>
          </div>
          </form>        
      </div>
    </div>
  )
}

export default FormValidation
