import React from 'react'

const Cards = () => {
    const [view, setView] = useState(false);
    const toggleView = () => {
        setView(!view)
    }
  return (
    <div className='py-12'>
          <div className="flex flex-row flex-wrap -mx-3 gap-2 justify-between">
              {[...Array(view ? 6 : 3)].map((_, index) => (
                  <div key={index} className="w-[33.33%] px-3 bg-red-500 max-w-[300px] h-[200px]">
                  </div>
              ))}
          </div>
          <div className='text-center mt-5'>
              <button className='p-3 font-bold text-lg bg-blue rounded' onClick={toggleView}>View Button</button>
          </div>
    </div>
  )
}

export default Cards
